let dataBuffer = (data) => {
  let letters = {
    lastFourteen: ''
  }

  let marker = 0;

  let checker = () => {
    for (var j = 0; j < letters.lastFourteen.length; j++) {
      if (letters[letters.lastFourteen[j]] > 1) {
        return false;
      }

    }
    return true;

  }

  let addLetter = (char) => {
    if (letters?.[char] === undefined) {
      letters[char] = 1;

    } else {
      letters[char]++;

    }

    letters.lastFourteen += char;
    marker++;
  }

  for (var i = 0; i < data.length; i++) {
    if (i < 14) {
      addLetter(data[i]);

      continue;
    }

    if (checker()) {
      return marker++;

    }

    addLetter(data[i]);

    letters.lastFourteen = letters.lastFourteen.slice(1, letters.lastFourteen.length);
    letters[data[i - 14]]--;
  }
}

