let dataBuffer = (data) => {
  let letters = {
    lastFour: ''
  }

  let marker = 0;

  let checker = () => {
    for (var j = 0; j < letters.lastFour.length; j++) {
      if (letters[letters.lastFour[j]] > 1) {
        return false;
      }

    }
    return true;

  }

  let addLetter = (char) => {
    if (letters?.[char] === undefined) {
      letters[char] = 1;

    } else {
      letters[char]++;

    }

    letters.lastFour += char;
    marker++;
  }

  for (var i = 0; i < data.length; i++) {
    if (i < 4) {
      addLetter(data[i]);

      continue;
    }

    if (checker()) {
      return marker++;

    }

    addLetter(data[i]);

    letters.lastFour = letters.lastFour.slice(1, letters.lastFour.length);
    letters[data[i - 4]]--;
  }
}

